	var id = mcName.substring(8);
	root.sc1.selectKeyValue.visible = false;
	root.sc1['mc_openchestbox_' + ChestBoxSelectNo].visible = false;
	var resultContainer = root.sc1.resultContainer;
	resultContainer.gotoAndPlay('open');
}
